import React from "react";
import { useState } from "react";

const films = [
  {
    id: 1,
    image:
      "data:image/jpeg;base64,",
    title: "Interstellar",
  },
  {
    id: 2,
    image:
      "data:image/jpeg;base64,",
    title: "Avenger: Civil War",
  },
  {
    id: 3,
    image:
      "data:image/jpeg;base64,",
    title: "Batman: The Dark Knight Rises",
  },
  {
    id: 4,
    image:
      "data:image/jpeg;base64,",
    title: "Top Gun Maverick",
  },
];

const ExampleArray = () => {
  const [movies, setMovies] = useState([]);

  if (movies.length === 0) {
    return (
      <div className="w-full max-w-[400px] mx-auto my-4 p-2.5 text-center shadow-2xl">
        <h1>NO MOVIES FOUND YET</h1>
        <button onClick={() => setMovies(films)} className="mybtn">
          Get Movies
        </button>
      </div>
    );
  }

  return (
    <div>
      <h1 className="my-4 font-bold text-3xl">Movies Available</h1>
      <section className="flex gap-10">
        {movies.map((movie) => {
          return (
            <div key={movie.id} className="w-[full] max-w-[300px]">
              <img
                src={movie.image}
                alt="title"
                className="w-[300px] h-[250px] object-fit"
              />
              <p className="my-4 font-bold text-xl">{movie.title}</p>
            </div>
          );
        })}
      </section>
    </div>
  );
};

export default ExampleArray;
